// String.prototype.startsWith for IE
if (!String.prototype.startsWith) {
    String.prototype.startsWith = function (searchString, position) {
        position = position || 0;
        return this.indexOf(searchString, position) === position;
    };
}

// Array.prototype.find function for IE
if (!Array.prototype.find) {
    Object.defineProperty(Array.prototype, "find", {
        value: function (predicate) {
            'use strict';
            if (this == null) {
                throw new TypeError('Array.prototype.find called on null or undefined');
            }
            if (typeof predicate !== 'function') {
                throw new TypeError('predicate must be a function');
            }
            var list = Object(this);
            var length = list.length >>> 0;
            var thisArg = arguments[1];
            var value;

            for (var i = 0; i < length; i++) {
                value = list[i];
                if (predicate.call(thisArg, value, i, list)) {
                    return value;
                }
            }
            return undefined;
        }
    });
}


// page init
jQuery(function(){
	initTabs();
	initFixedScrollBlock();
	initCheckAll();
	initCarousel();
	initOpenClose();
	initPopups();
	initAddClasses();
});

// scroll galleries init
function initCarousel() {
	jQuery('.carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.carousel-slide',
		btnPrev: '.btn-prev',
		btnNext: '.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('.sm-carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.sm-slide',
		btnPrev: '.btn-prev',
		btnNext: '.btn-next',
		pagerLinks: '.pagination li',
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// content tabs init
function initTabs() {
	jQuery('.tabset').tabset({
		tabLinks: 'a',
		addToParent: true,
		defaultTab: false
	});
}

// open-close init
function initOpenClose() {
	function extraTop() {
		var totalOffset = 0;

		jQuery('#header, .topbar, .slide-bar').each(function(){
			totalOffset += jQuery(this).outerHeight();
		});

		return totalOffset;
	}

	jQuery('.main-block, .slide-panel').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide',
		onInit: function() {
			var self = this;

			self.inst = jQuery('#main').data('FixedScrollBlock');

			if (self.inst && self.inst.options.slideBlock === '.tools-nav') {
				setTimeout(function() {
					self.inst.options.extraTop = extraTop();
					jQuery(window).trigger('resize');
				}, 10);
			}

		},
		animEnd: function(self) {
			var self = this;

			if (self.inst && self.inst.options.slideBlock === '.tools-nav') {
				setTimeout(function() {
					self.inst.options.extraTop = extraTop();
					jQuery(window).trigger('resize');
				}, 10);
			}
		}
	});
	jQuery('.table-block tr').openClose({
		activeClass: 'active',
		opener: '.table-opener',
		slider: '.table-slide',
		animSpeed: 400,
		effect: 'none'
	});
}

// popups init
function initPopups() {
	jQuery('.popup').contentPopup({
		mode: 'click',
		popup: '.drop',
		btnOpen: '.btn-burger'
	});
}

// initialize fixed blocks on scroll
function initFixedScrollBlock() {
	jQuery('#main').fixedScrollBlock({
		slideBlock: '.tools-nav',
		positionType: 'absolute',
		extraTop: 270
	});
}

// add class on click
function initAddClasses() {
	jQuery('.link').clickClass({
		classAdd: 'table-active',
		addToParent: 'table-content'
	});
}

// load more init
function initCheckAll() {
	jQuery('[data-check-pattern]').checkAll();
}

